  = 4,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &lt;span class=&#34;type&#34;&gt;AVAudioUnitReverbPresetPlate&lt;/span&gt;           = 5,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &lt;span class=&#34;type&#34;&gt;AVAudioUnitReverbPresetMediumChamber&lt;/span&gt;   = 6,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &lt;span class=&#34;type&#34;&gt;AVAudioUnitReverbPresetLargeChamber&lt;/span&gt;    = 7,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &lt;span class=&#34;type&#34;&gt;AVAudioUnitReverbPresetCathedral&lt;/span&gt;       = 8,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &lt;span class=&#34;type&#34;&gt;AVAudioUnitReverbPresetLargeRoom2&lt;/span&gt;      = 9,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &lt;span class=&#34;type&#34;&gt;AVAudioUnitReverbPresetMediumHall2&lt;/span&gt;     = 10,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &lt;span class=&#34;type&#34;&gt;AVAudioUnitReverbPresetMediumHall3&lt;/span&gt;     = 11,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &lt;span class=&#34;type&#34;&gt;AVAudioUnitReverbPresetLargeHall2&lt;/span&gt;      = 12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &amp;#125; &lt;span class=&#34;type&#34;&gt;NS_ENUM_AVAILABLE&lt;/span&gt;(10_10, 8_0);&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;br /&gt;
